import React, { useState } from 'react';

const DataEntryForm = () => {
  const [formData, setFormData] = useState({
    email: '',
    fullName: '',
    address: '',
    address2: '',
    city: '',
    province: '',
    postalCode: '',
    terms: false
  });

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData({
      ...formData,
      [name]: type === 'checkbox' ? checked : value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Display form data (Part II)
    console.log(formData);
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Data Entry Form</h2>

      <label>Email</label>
      <input type="email" name="email" value={formData.email} onChange={handleChange} placeholder="Enter email" />

      <label>Full Name</label>
      <input type="text" name="fullName" value={formData.fullName} onChange={handleChange} placeholder="Full Name" />

      <label>Address</label>
      <input type="text" name="address" value={formData.address} onChange={handleChange} placeholder="1234 Main St" />

      <label>Address 2</label>
      <input type="text" name="address2" value={formData.address2} onChange={handleChange} placeholder="Apartment, studio, or floor" />

      <label>City</label>
      <input type="text" name="city" value={formData.city} onChange={handleChange} />

      <label>Province</label>
      <select name="province" value={formData.province} onChange={handleChange}>
        <option value="">Choose...</option>
        <option value="Alberta">Alberta</option>
        <option value="British Columbia">British Columbia</option>
        <option value="Manitoba">Manitoba</option>
        <option value="New Brunswick">New Brunswick</option>
        <option value="Newfoundland and Labrador">Newfoundland and Labrador</option>
        <option value="Nova Scotia">Nova Scotia</option>
        <option value="Ontario">Ontario</option>
        <option value="Prince Edward Island">Prince Edward Island</option>
        <option value="Quebec">Quebec</option>
        <option value="Saskatchewan">Saskatchewan</option>
      </select>

      <label>Postal Code</label>
      <input type="text" name="postalCode" value={formData.postalCode} onChange={handleChange} />

      <label>
        <input type="checkbox" name="terms" checked={formData.terms} onChange={handleChange} />
        Agree Terms & Condition?
      </label>

      <button type="submit">Submit</button>
    </form>
  );
};

export default DataEntryForm;
